mapper.select:
SELECT item.item_type_id `key`,

FROM sale_record
LEFT JOIN item_detail ON sale_record.item_detail_id = item_detail.id
LEFT JOIN item ON item.id = item_detail.item_id
WHERE sale_record.create_at BETWEEN #{from} AND #{to}

GROUP BY `key`;

SELECT item_detail.item_id `key`,

FROM sale_record
LEFT JOIN item_detail ON sale_record.item_detail_id = item_detail.id
WHERE sale_record.create_at BETWEEN #{from} AND #{to}

GROUP BY `key`;

SELECT item_detail.item_size_id `key`,

FROM sale_record
LEFT JOIN item_detail ON sale_record.item_detail_id = item_detail.id
WHERE sale_record.create_at BETWEEN #{from} AND #{to}

GROUP BY `key`;

SELECT item_type.sex `key`,
FROM sale_record LEFT JOIN item_detail ON sale_record.item_detail_id = item_detail.id LEFT JOIN item ON item.id = item_detail.item_id LEFT JOIN item_type ON item_type.id = item.item_type_id WHERE sale_record.create_at BETWEEN #{from} AND #{to}
GROUP BY `key`;
